module MUX16_2_1(
    input [15:0] signal1,
    input [15:0] signal2,
    input control,
    output reg [15:0] signal
    );

	 always @ (signal1 or signal2 or control)
	 begin
		if(control==0)
			signal = signal1;
		else
			signal = signal2;
	 end


endmodule
